import React, { useEffect, useState } from "react";
import { useHistory } from "react-router-dom";
import { request } from "../../utils/api-utils";
import { isLoggedIn } from "../../utils/user-utils";
import styles from './styles';



const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const history = useHistory();

    useEffect(() => {
        if(isLoggedIn()) {
            history.replace('/dahsboard')
        }
    }, [history])

    const doLogin = (e) => {
       console.log(username, password); 
       request.post(`/api/user/login`, {
           email: username,
           password: password
       })
       .then()
       .catch(error => {
           if(error) {
                alert(error);
           }
       })
       e.preventDefault();
    }

    const renderLoginForm = () => {
        return <form onSubmit={e => {doLogin(e)}}>
            <div className="form-group">
                <input 
                    type="text" 
                    name="username" 
                    className="input-style" 
                    placeholder="Enter username" 
                    onChange={(e) => setUsername(e.target.value)}
                    value={username}
                />
            </div>
            <div className="form-group">
                <input 
                    type="password" 
                    name="pass" 
                    className="input-style" 
                    placeholder="Enter password" 
                    onChange={(e) => setPassword(e.target.value)}
                    value={password}
                />
            </div>
            <div className="form-group">
                <button type="submit" className="btn btn-primary btn-expand">Login</button>
            </div>
        </form>
    }

    return <div style={styles.loginContainer}>
        <div style={styles.loginBox}>
            <h3 className="title">Login</h3>
            {renderLoginForm()}
        </div>
    </div>
}
export default Login;
