import React, { useEffect, useState } from 'react';
import './styles.css';
import styled from 'styled-components';
import { request } from '../../utils/api-utils';
import { getToken } from '../../utils/user-utils';
import Modal from '../modal/Modal';
const AppCard = styled.div`
    background: #fff;
    padding: 20px 20px;
    transition: 0.5s all ease-out;
    filter: drop-shadow(0 0 1px #ccc);
    border-radius: 10px;
    height: auto;
`;
const TaskList = styled.ul`
    margin: 0;
    padding: 0;
    list-style: none;
`;
const ListItem = styled.li`
    padding: 15px 0;
    border-top: 1px solid #eee;
    :first-of-type {
        border-top: none;
    }
`;
const TaskDetails = (props) => {
    const {title} = props
    const [tasks, setTasks] = useState([])
    const [show, setShowModal] = useState(false);

    useEffect(() => {
        getTaskLists();
    },[])

    const showModal = () => {
        setShowModal(true);
    }

    const hideModal = () => {
        setShowModal(false);
    }

    const getTaskLists = () => {
        request.get('/tasks', {headers: { 'x-auth-token': getToken() }})
        .then((response) => {
            if(response) {
                if(response.data.status === false) {
                    console.log(response.data.message);
                    return;
                }
                setTasks(response.data);
            }
        })
        .catch(error => {
            if(error) {
                console.log(error);
            }
        })
    }

    return <>
        <div className="clear">
            <div className="left"><h3 className="title">{title}</h3></div>
            <button type="button" className="btn btn-primary right" onClick={showModal}>
                <span className="material-icons" style={{fontSize:'unset'}}>add</span>New Task
            </button>
            <input className="input-style search-input right" placeholder="Search by task name"></input>
        </div>
        <div>
            <AppCard>
                <TaskList>
                    {
                        tasks.map((task, index) => (
                           (task.completed ? <ListItem className="clear">
                                    <label className="left">
                                        <input type="checkbox" checked/>
                                        <del>{task.name}</del>
                                    </label>
                                    <div className="right">
                                        <span className="material-icons text-gray" style={{marginRight:'10px'}}>edit</span>
                                        <span className="material-icons text-gray">delete</span>
                                    </div>
                            </ListItem> : <ListItem className="clear">
                                <label className="left">
                                    <input type="checkbox" />
                                    <span>{task.name}</span>
                                </label>
                                <div className="right">
                                    <span className="material-icons text-gray" style={{marginRight:'10px'}}>edit</span>
                                    <span className="material-icons text-gray">delete</span>
                                </div>
                            </ListItem>)
                        ))
                    }
                </TaskList>
            </AppCard>
        </div>
        <Modal show={show} handleClose={hideModal}>
            <h3 className="title">New Task</h3>
        </Modal>
    </>
}

export default TaskDetails;
