import React from 'react';
import './styles.css';
import styled from 'styled-components';
const AppCard = styled.div`
    background: #fff;
    padding: 20px 20px;
    transition: 0.5s all ease-out;
    filter: drop-shadow(0 0 1px #ccc);
    border-radius: 10px;
    height: 120px;
`;
const TaskDetails = (props) => {
    const {title} = props
    return <>
        <div className="clear">
            <div className="left"><h3 className="title">{title}</h3></div>
            <button type="button" className="btn btn-primary right"><span className="material-icons" style={{fontSize:'unset'}}>add</span>New Task</button>
            <input className="input-style search-input right" placeholder="Search by task name"></input>
        </div>
        <div >
            <AppCard></AppCard>
        </div>
    </>
}

export default TaskDetails;
