import React, { useEffect, useState } from 'react';
import './styles.css';
import styled from 'styled-components';
import { request } from '../../utils/api-utils';
import { getToken } from '../../utils/user-utils';
import Modal from '../modal/Modal';
const AppCard = styled.div`
    background: #fff;
    padding: 20px 20px;
    transition: 0.5s all ease-out;
    filter: drop-shadow(0 0 1px #ccc);
    border-radius: 10px;
    height: auto;
`;
const TaskList = styled.ul`
    margin: 0;
    padding: 0;
    list-style: none;
`;
const ListItem = styled.li`
    padding: 15px 0;
    border-top: 1px solid #eee;
    :first-of-type {
        border-top: none;
    }
`;
const TaskDetails = (props) => {
    const {title} = props
    const [tasks, setTasks] = useState([])
    const [show, setShowModal] = useState(false)
    const [taskName, setTaskName] = useState('')

    useEffect(() => {
        getTaskLists();
    },[])

    const showModal = () => {
        setShowModal(true);
    }

    const hideModal = () => {
        setShowModal(false);
    }

    const getTaskLists = () => {
        request.get('/tasks', {headers: { 'x-auth-token': getToken() }})
        .then((response) => {
            if(response) {
                if(response.data.status === false) {
                    console.log(response.data.message);
                    return;
                }
                setTasks(response.data);
            }
        })
        .catch(error => {
            if(error) {
                console.log(error);
            }
        })
    }

    const saveTask = (e) => {
        const header = {
            headers: { 'x-auth-token': getToken() }
        }
        request.post(`/tasks`, {
            name: taskName,
            completed: false
        }, header)
        .then((response) => {
            if(response.data.status === false) {
                alert(response.data.message);
                return;
            }
            hideModal()
            getTaskLists()
            props.getDashboardData();
        })
        .catch(error => {
             if(error) {
                 console.log(error);
             }
        })
        e.preventDefault();
    }

    const updateTask = (id, name, completed) => {
        //console.log(id, name);return;
        const header = {
            headers: { 'x-auth-token': getToken() }
        }
        request.put(`/tasks/${id}`, {
            name: name,
            completed: completed
        }, header)
        .then((response) => {
            if(response.data.status === false) {
                alert(response.data.message);
                return;
            }
            getTaskLists()
            props.getDashboardData();
        })
        .catch(error => {
            if(error) {
                console.log(error);
            }
        })
    }


    return <>
        <div className="clear">
            <div className="left"><h3 className="title">{title}</h3></div>
            <button type="button" className="btn btn-primary right" onClick={showModal}>
                + New Task
            </button>
            <input className="input-style search-input right" placeholder="Search by task name"></input>
        </div>
        <div>
            <AppCard>
                <TaskList>
                    {
                        tasks.map((task, index) => (
                           (task.completed ? <ListItem className="clear" key={index}>
                                    <label className="left">
                                        <input type="checkbox" checked readOnly/>
                                        <del>{task.name}</del>
                                    </label>
                                    <div className="right">
                                        <span className="material-icons text-light-gray" style={{marginRight:'10px'}}>edit</span>
                                        <span className="material-icons text-light-gray">delete</span>
                                    </div>
                            </ListItem> : <ListItem className="clear" key={index}>
                                <label className="left">
                                    <input type="checkbox" value={task._id} onChange={(e) => {updateTask(e.target.value, task.name, true)}} />
                                    <span>{task.name}</span>
                                </label>
                                <div className="right">
                                    <span onClick={updateTask(task._id, task.name, faslse)} className="material-icons text-gray" style={{marginRight:'10px'}}>edit</span>
                                    <span className="material-icons text-gray">delete</span>
                                </div>
                            </ListItem>)
                        ))
                    }
                </TaskList>
            </AppCard>
        </div>
        <Modal show={show} handleClose={hideModal}>
            <h3 className="title">New Task</h3>
            <form onSubmit={e => {saveTask(e)}}>
                <div className="form-group">
                    <input 
                        onChange={(e) => setTaskName(e.target.value)} 
                        value={taskName} 
                        type="text" 
                        className="input-style" 
                        placeholder="Task Name" 
                    />
                </div>
                <div className="form-group">
                    <button className="btn btn-primary btn-expand no-pad">+ New Task</button>
                </div>
            </form>
        </Modal>
    </>
}

export default TaskDetails;
