export const getToken = () => {
    if(localStorage.AUTH_TOKEN && localStorage.AUTH_TOKEN !== '') {
        return localStorage.AUTH_TOKEN;
    }
    return null;
}

export const isLoggedIn = () => {
    const token = getToken();
    return token && token !== null ? true : false;
}
