import React, { useEffect, useState } from "react";
import './styles.css';
import styled from 'styled-components';
import {PieChart, Pie, Legend, Tooltip, ResponsiveContainer, Cell} from 'recharts';
import { getToken } from "../../utils/user-utils";

const AppCard = styled.div`
    background: #fff;
    padding: 20px 20px;
    transition: 0.5s all ease-out;
    filter: drop-shadow(0 0 1px #ccc);
    border-radius: 10px;
    height: 120px;
`;
const CompletedTask = styled.label`
    color: #1976d2;
    font-size: 50px;
`;

const TaskCard = (props) => {
    const {title, type} = props;
    const [data, setData] = useState([]);
    useEffect(()=> {
        request.get(`/dashboard`, getToken())
        .then((response) => {
             console.log(response);
            //  if(response.data.status === false) {
            //      alert(response.data.message);
            //      return;
            //  }
        })
        .catch(error => {
             if(error) {
                 console.log(error);
             }
        })
        setData([
            {name: '', value: 25},
            {name: "", value: 75}
        ])
    },[])
    const renderCounter = () => {
        return <div style={{marginTop: '20px'}}>
            <CompletedTask>5</CompletedTask>/
            <label>20</label>
        </div>
    }

    const renderList = () => {
        return <ul className="card-list">
            <li>Clean the room</li>
            <li>Production deployment</li>
            <li><del>Dinner sex</del></li>
        </ul>
    }

    const renderChart = () => {
        return <div className="chart-container">
             <ResponsiveContainer width="100%" height="100%" minHeight={100}>
            <PieChart>
            <Pie
                dataKey="value"
                isAnimationActive={false}
                data={data}
                fill="#8884d8"
                label
                labelLine={true}
            >
                {data.map((entry, index) => (
                    <Cell
                        dataKey="name"
                        key={`cell-${index}`}
                        fill={COLORS[index % COLORS.length]}
                    />
                ))}
            </Pie>
            <Tooltip />
            </PieChart>
      </ResponsiveContainer>
        </div>

    }

    const renderCardContent = () => {
        if(type === 'TASK_COUNTER') {
            return renderCounter()
        }

        else if(type === 'TASK_LIST') {
           return renderList()
        }

        else if(type === 'TASK_CHART') {
            return renderChart()
        }
    }


    return <AppCard>
        <h3 className="card-title"> {title??""}</h3>
        {renderCardContent()}
    </AppCard>
}

const COLORS = ['#1890ff', '#ddd'];

const RADIAN = Math.PI / 180;
const renderLabel = ({
  cx, cy, midAngle, innerRadius, outerRadius, percent, index, name
}) => {
   const radius = innerRadius + (outerRadius - innerRadius) * 0.5;
  const x = cx + radius * Math.cos(-midAngle * RADIAN);
  const y = cy + radius * Math.sin(-midAngle * RADIAN);

  return (
    <text font-size="11" x={x} y={y} fill="#444" textAnchor={x > cx ? 'start' : 'end'} dominantBaseline="central">
      {`${name}`}
    </text>
  );
}

export default TaskCard;
